export function goUpByTheTree(root) {
  const allIds = new Set();
  let current = root;
  const highlightedId = root.uniqueId;
  allIds.add(current.uniqueId);
  let limit = 2;

  while (current && limit > 0) {
    limit--;
    current = current.getParent();

    if (current) {
      allIds.add(current.uniqueId);
      root = current;
    }
  }

  return {
    allIds,
    highlightedId,
    root
  };
}