"use strict";

var _evalTemplate = require("./evalTemplate");

describe("evalTemplate", () => {
  test("basic", () => {
    const res = (0, _evalTemplate.evalTemplate)("https://example.com/${filePath}${ext}", {
      filePath: "test",
      ext: ".js"
    });
    expect(res).toBe("https://example.com/test.js");
  });
});