"use strict";

var _transformPath = require("./transformPath");

describe("transformPath", () => {
  test("replacing internal url to external", () => {
    expect((0, _transformPath.transformPath)("/app/src/myFile.js", "/app/", "C://myPath/")).toBe("C://myPath/src/myFile.js");
  });
  test("unifying absolute and relative paths", () => {
    const from = "^/src/";
    const to = "/myPath/src/";
    expect((0, _transformPath.transformPath)("/src/myFile.js", from, to)).toBe("/myPath/src/myFile.js");
    expect((0, _transformPath.transformPath)("/myPath/src/myFile.js", from, to)).toBe("/myPath/src/myFile.js");
  });
});